styles:
/* Animations*/
@keyframes ILS_shake {
  from, to {
    transform: translate3d(0, 0, 0);
  }
  10%, 30%, 50%, 70%, 90% {
    transform: translate3d(-10px, 0, 0);
  }
  20%, 40%, 60%, 80% {
    transform: translate3d(10px, 0, 0);
  }
}
@keyframes ILS_buttonPress {
  0% {
    background-color: #acafb2;
  }
  100% {
    background-color: #f9f9f9;
  }
}
/* Lock Screen Layout*/
.ISL_close-icon {
  top: 3%;
  right: 7%;
  color: black;
  font-size: 26px;
  font-weight: 500;
  position: absolute;
  padding-left: 30px;
  padding-bottom: 30px;
  font-family: Arial, sans-serif;
}
.ILS_lock {
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 999;
  padding: 65px 0;
  background-color: #f9f9f9;
}
.ILS_lock-hidden {
  display: none;
}
.ILS_input-content {
  display: flex;
  flex-direction: column;
  justify-content: center;
  flex: 1;
}
.ILS_label-row {
  height: 50px;
  width: 100%;
  text-align: center;
  padding-top: 10px;
  color: #141414;
}
.ILS_label_title {
  font-size: 34px;
  text-transform: uppercase;
}
.ILS_label_subTitle {
  font-size: 14px;
  padding-top: 0;
}
.ILS_label_link {
  color: #666666 !important;
  font-size: 14px !important;
  padding-top: 26px !important;
  background-color: transparent !important;
}
.ILS_del {
  padding: 0 !important;
  margin: 25px 48px !important;
  box-sizing: border-box;
  position: relative;
  display: block;
  width: 14px !important;
  height: 14px !important;
  transform: scale(var(--ggs,1));
  border: 2px solid;
  border-left: 0;
  color: #666666 !important;
  border-radius: 0px !important;
  border-top-right-radius: 2px !important;
  background-color: transparent !important;
  border-bottom-right-radius: 2px !important;
}
  .ILS_del::after,
  .ILS_del::before {
  content: "";
  display: block;
  box-sizing: border-box;
  position: absolute
  }

  .ILS_del::before {
  background:
  linear-gradient( to left,
  currentColor 18px,transparent 0)
  no-repeat center center/10px 2px;
  border-right: 3px solid transparent;
  box-shadow: inset 0 0 0 2px;
  right: 2px;
  bottom: 1px;
  width: 8px;
  height: 8px;
  border-left: 3px solid transparent;
  transform: rotate(45deg)
  }

.ILS_del::after {
  width: 10px;
  height: 10px;
  border-top: 2px solid;
  border-left: 2px solid;
  border-top-left-radius: 1px;
  transform: rotate(-45deg);
  top: 0;
  left: -5px
  } 
.ILS_circles-row {
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  height: 50px;
}
.ILS_circle {
  background-color: transparent;
  border-radius: 50%;
  width: 12px;
  height: 12px;
  border: solid 1.9px #141414;
  margin: 0 15px;
}
.ILS_numbers-row {
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  height: 85px;
}
.ILS_digit {
  margin: 0 20px;
  width: 68px;
  border-radius: 50%;
  height: 68px;
  text-align: center;
  padding-top: 22px;
  font-size: 30px;
  color: #141414;
  font-weight: bold;
  background-color: transparent;
}
.ILS_digit.activated {
  -webkit-animation-name: ILS_buttonPress;
  animation-name: ILS_buttonPress;
  -webkit-animation-duration: 1;
  animation-duration: 1s;
}
.ILS_full {
  background-color:#141414!important;
}
.ILS_shake {
  -webkit-animation-name: ILS_shake;
  animation-name: ILS_shake;
  -webkit-animation-duration: 0.5;
  animation-duration: 0.5s;
  -webkit-animation-fill-mode: both;
  animation-fill-mode: both;
}
